import React, { useState } from 'react';
import './App.css';

// 샘플 데이터 타입 정의
interface Question {
  id: number;
  type: 'multiple' | 'subjective';
  difficulty: 'easy' | 'medium' | 'hard';
  question: string;
  options?: string[];
  correctAnswer: string;
  userAnswer?: string;
  isCorrect?: boolean;
}

// 샘플 문제 데이터
const sampleQuestions: Question[] = [
  {
    id: 1,
    type: 'multiple',
    difficulty: 'easy',
    question: 'FineTeaching의 주요 기능은 무엇인가요?',
    options: ['음성 요약', '문제 생성', '학습 보조', '모든 것'],
    correctAnswer: '모든 것'
  },
  {
    id: 2,
    type: 'subjective',
    difficulty: 'medium',
    question: 'FineTeaching에서 지원하는 음성 파일 형식을 두 가지 이상 작성하세요.',
    correctAnswer: 'm4a, mp3'
  },
  {
    id: 3,
    type: 'multiple',
    difficulty: 'hard',
    question: '문제풀이 인터페이스에서 제공하는 필터 기능이 아닌 것은?',
    options: ['객관식만 보기', '주관식만 보기', '난이도 필터', '시간 제한 설정'],
    correctAnswer: '시간 제한 설정'
  }
];

function App() {
  const [currentStep, setCurrentStep] = useState<'intro' | 'upload' | 'quiz'>('intro');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [questions, setQuestions] = useState<Question[]>(sampleQuestions);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState<{[key: number]: string}>({});
  const [filter, setFilter] = useState<{
    type: 'all' | 'multiple' | 'subjective';
    difficulty: 'all' | 'easy' | 'medium' | 'hard';
    showWrongOnly: boolean;
  }>({
    type: 'all',
    difficulty: 'all',
    showWrongOnly: false
  });

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      setCurrentStep('upload');
    }
  };

  const handleStartQuiz = () => {
    setCurrentStep('quiz');
    setCurrentQuestionIndex(0);
    setUserAnswers({});
  };

  const handleAnswerSubmit = (answer: string) => {
    const currentQuestion = questions[currentQuestionIndex];
    const isCorrect = answer === currentQuestion.correctAnswer;

    setUserAnswers(prev => ({
      ...prev,
      [currentQuestion.id]: answer
    }));

    setQuestions(prev => prev.map(q => 
      q.id === currentQuestion.id 
        ? { ...q, userAnswer: answer, isCorrect }
        : q
    ));
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
    }
  };

  const filteredQuestions = questions.filter(q => {
    if (filter.type !== 'all' && q.type !== filter.type) return false;
    if (filter.difficulty !== 'all' && q.difficulty !== filter.difficulty) return false;
    if (filter.showWrongOnly && q.isCorrect !== false) return false;
    return true;
  });

  const currentQuestion = filteredQuestions[currentQuestionIndex] || questions[0];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="container mx-auto px-4 py-8">
        {/* 헤더 */}
        <header className="text-center mb-8">
          <h1 className="text-4xl font-bold text-indigo-800 mb-2">FineTeaching</h1>
          <p className="text-lg text-gray-600">
            강의 음성을 자동으로 요약하고 문제를 생성해주는 학습 보조 웹앱
          </p>
        </header>

        {/* 메인 컨텐츠 */}
        <main className="max-w-4xl mx-auto">
          {currentStep === 'intro' && (
            <div className="bg-white rounded-lg shadow-lg p-8 text-center">
              <h2 className="text-2xl font-semibold text-gray-800 mb-4">
                FineTeaching에 오신 것을 환영합니다
              </h2>
              <p className="text-gray-600 mb-6 leading-relaxed">
                FineTeaching은 강의 음성 파일을 업로드하면 자동으로 요약하고, 
                학습 내용을 바탕으로 문제를 생성해주는 혁신적인 학습 보조 도구입니다. 
                객관식과 주관식 문제를 통해 학습 효과를 극대화하세요.
              </p>
              <button
                onClick={() => setCurrentStep('upload')}
                className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors"
              >
                시작하기
              </button>
            </div>
          )}

          {currentStep === 'upload' && (
            <div className="bg-white rounded-lg shadow-lg p-8">
              <h2 className="text-2xl font-semibold text-gray-800 mb-6 text-center">
                음성 파일 업로드
              </h2>

              <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center mb-6">
                <input
                  type="file"
                  accept=".m4a,.mp3,.wav"
                  onChange={handleFileUpload}
                  className="hidden"
                  id="file-upload"
                />
                <label
                  htmlFor="file-upload"
                  className="cursor-pointer bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors inline-block"
                >
                  파일 선택하기
                </label>
                <p className="text-gray-500 mt-2">
                  지원 형식: M4A, MP3, WAV
                </p>
              </div>

              {selectedFile && (
                <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
                  <p className="text-green-800 font-medium">
                    선택된 파일: {selectedFile.name}
                  </p>
                  <p className="text-green-600 text-sm">
                    파일 크기: {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                  </p>
                </div>
              )}

              {selectedFile && (
                <div className="flex gap-4 justify-center">
                  <button className="bg-green-600 hover:bg-green-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors">
                    요약 다운로드
                  </button>
                  <button
                    onClick={handleStartQuiz}
                    className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-3 px-6 rounded-lg transition-colors"
                  >
                    문제 풀기
                  </button>
                </div>
              )}
            </div>
          )}

          {currentStep === 'quiz' && (
            <div className="bg-white rounded-lg shadow-lg p-8">
              {/* 필터 섹션 */}
              <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                <h3 className="text-lg font-semibold text-gray-800 mb-3">필터</h3>
                <div className="flex flex-wrap gap-4">
                  <select
                    value={filter.type}
                    onChange={(e) => setFilter(prev => ({ ...prev, type: e.target.value as any }))}
                    className="px-3 py-2 border border-gray-300 rounded-md"
                  >
                    <option value="all">모든 문제</option>
                    <option value="multiple">객관식만</option>
                    <option value="subjective">주관식만</option>
                  </select>

                  <select
                    value={filter.difficulty}
                    onChange={(e) => setFilter(prev => ({ ...prev, difficulty: e.target.value as any }))}
                    className="px-3 py-2 border border-gray-300 rounded-md"
                  >
                    <option value="all">모든 난이도</option>
                    <option value="easy">쉬움</option>
                    <option value="medium">보통</option>
                    <option value="hard">어려움</option>
                  </select>

                  <label className="flex items-center">
                    <input
                      type="checkbox"
                      checked={filter.showWrongOnly}
                      onChange={(e) => setFilter(prev => ({ ...prev, showWrongOnly: e.target.checked }))}
                      className="mr-2"
                    />
                    틀린 문제만 보기
                  </label>
                </div>
              </div>

              {/* 문제 표시 */}
              <div className="mb-6">
                <div className="flex justify-between items-center mb-4">
                  <span className="text-sm text-gray-500">
                    문제 {currentQuestionIndex + 1} / {filteredQuestions.length}
                  </span>
                  <span className={`px-2 py-1 rounded text-xs font-medium ${
                    currentQuestion.difficulty === 'easy' ? 'bg-green-100 text-green-800' :
                    currentQuestion.difficulty === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-red-100 text-red-800'
                  }`}>
                    {currentQuestion.difficulty === 'easy' ? '쉬움' :
                     currentQuestion.difficulty === 'medium' ? '보통' : '어려움'}
                  </span>
                </div>

                <h3 className="text-xl font-semibold text-gray-800 mb-4">
                  {currentQuestion.question}
                </h3>

                {currentQuestion.type === 'multiple' && currentQuestion.options && (
                  <div className="space-y-3">
                    {currentQuestion.options.map((option, index) => (
                      <button
                        key={index}
                        onClick={() => handleAnswerSubmit(option)}
                        className={`w-full text-left p-3 rounded-lg border transition-colors ${
                          userAnswers[currentQuestion.id] === option
                            ? 'bg-indigo-100 border-indigo-300'
                            : 'bg-white border-gray-300 hover:bg-gray-50'
                        }`}
                      >
                        {option}
                      </button>
                    ))}
                  </div>
                )}

                {currentQuestion.type === 'subjective' && (
                  <div className="space-y-3">
                    <textarea
                      placeholder="답을 입력하세요..."
                      className="w-full p-3 border border-gray-300 rounded-lg resize-none"
                      rows={3}
                      value={userAnswers[currentQuestion.id] || ''}
                      onChange={(e) => handleAnswerSubmit(e.target.value)}
                    />
                  </div>
                )}

                {userAnswers[currentQuestion.id] && (
                  <div className="mt-4 p-3 rounded-lg bg-gray-50">
                    <p className="text-sm text-gray-600">제출한 답: {userAnswers[currentQuestion.id]}</p>
                    {currentQuestion.isCorrect !== undefined && (
                      <p className={`text-sm font-medium ${
                        currentQuestion.isCorrect ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {currentQuestion.isCorrect ? '정답입니다!' : '틀렸습니다.'}
                      </p>
                    )}
                  </div>
                )}
              </div>

              {/* 네비게이션 버튼 */}
              <div className="flex justify-between">
                <button
                  onClick={() => setCurrentStep('intro')}
                  className="bg-gray-500 hover:bg-gray-600 text-white font-semibold py-2 px-4 rounded-lg transition-colors"
                >
                  처음으로
                </button>

                {currentQuestionIndex < filteredQuestions.length - 1 && (
                  <button
                    onClick={handleNextQuestion}
                    className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-lg transition-colors"
                  >
                    다음 문제
                  </button>
                )}
              </div>
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default App;
