import React, { useState } from 'react';

interface Question {
  id: number;
  type: 'multiple' | 'subjective';
  question: string;
  options?: string[];
  correctAnswer: string;
  difficulty: 'easy' | 'medium' | 'hard';
}

const sampleQuestions: Question[] = [
  {
    id: 1,
    type: 'multiple',
    question: 'React에서 상태를 관리하는 Hook은 무엇인가요?',
    options: ['useState', 'useEffect', 'useContext', 'useReducer'],
    correctAnswer: 'useState',
    difficulty: 'easy'
  },
  {
    id: 2,
    type: 'subjective',
    question: 'React의 주요 특징 중 하나를 설명해주세요.',
    correctAnswer: '컴포넌트 기반',
    difficulty: 'medium'
  },
  {
    id: 3,
    type: 'multiple',
    question: 'JSX에서 조건부 렌더링을 할 때 사용하는 방법은?',
    options: ['if문', '삼항 연산자', 'switch문', 'for문'],
    correctAnswer: '삼항 연산자',
    difficulty: 'easy'
  },
  {
    id: 4,
    type: 'subjective',
    question: 'Props와 State의 차이점을 설명해주세요.',
    correctAnswer: 'Props는 읽기 전용, State는 변경 가능',
    difficulty: 'hard'
  },
  {
    id: 5,
    type: 'multiple',
    question: 'React에서 리스트를 렌더링할 때 필요한 prop은?',
    options: ['id', 'key', 'name', 'value'],
    correctAnswer: 'key',
    difficulty: 'medium'
  }
];

function App() {
  const [currentStep, setCurrentStep] = useState<'intro' | 'quiz'>('intro');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [userAnswers, setUserAnswers] = useState<{ [key: number]: string }>({});
  const [showResults, setShowResults] = useState(false);
  const [filterType, setFilterType] = useState<'all' | 'multiple' | 'subjective'>('all');
  const [filterDifficulty, setFilterDifficulty] = useState<'all' | 'easy' | 'medium' | 'hard'>('all');
  const [showIncorrectOnly, setShowIncorrectOnly] = useState(false);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const handleStartQuiz = () => {
    setCurrentStep('quiz');
    setCurrentQuestionIndex(0);
    setUserAnswers({});
    setShowResults(false);
  };

  const handleAnswerSelect = (answer: string) => {
    setSelectedAnswer(answer);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer) {
      setUserAnswers(prev => ({
        ...prev,
        [sampleQuestions[currentQuestionIndex].id]: selectedAnswer
      }));

      if (currentQuestionIndex < sampleQuestions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setSelectedAnswer('');
      } else {
        setShowResults(true);
      }
    }
  };

  const goHome = () => {
    setCurrentStep('intro');
    setSelectedFile(null);
    setCurrentQuestionIndex(0);
    setUserAnswers({});
    setShowResults(false);
    setSelectedAnswer('');
  };

  const goBack = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
      setSelectedAnswer(userAnswers[sampleQuestions[currentQuestionIndex - 1].id] || '');
    }
  };

  const filteredQuestions = sampleQuestions.filter(q => {
    if (filterType !== 'all' && q.type !== filterType) return false;
    if (filterDifficulty !== 'all' && q.difficulty !== filterDifficulty) return false;
    if (showIncorrectOnly) {
      const userAnswer = userAnswers[q.id];
      return userAnswer && userAnswer !== q.correctAnswer;
    }
    return true;
  });

  const currentQuestion = filteredQuestions[currentQuestionIndex] || sampleQuestions[0];

  if (currentStep === 'intro') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
        <div className="max-w-4xl w-full bg-white rounded-2xl shadow-xl p-8">
          {/* 헤더 */}
          <div className="text-center mb-8">
            <h1 className="text-4xl font-medium text-gray-800 mb-3 tracking-tight">FineTeaching</h1>
            <p className="text-gray-600 text-lg leading-relaxed">강의 음성을 자동으로 요약하고 문제를 생성해주는 학습 보조 웹앱</p>
          </div>

          {/* 메인 콘텐츠 */}
          <div className="grid md:grid-cols-2 gap-8 items-center">
            {/* 왼쪽: 소개 */}
            <div className="space-y-6">
              <div className="bg-blue-50 rounded-xl p-6">
                <h2 className="text-xl font-medium text-gray-800 mb-4">간단한 3단계</h2>
                <div className="space-y-4">
                  <div className="flex items-center gap-4">
                    <div className="w-10 h-10 bg-blue-500 text-white rounded-full flex items-center justify-center text-sm font-medium">1</div>
                    <span className="text-gray-700 text-base">음성 파일 업로드</span>
                  </div>
                  <div className="flex items-center gap-4">
                    <div className="w-10 h-10 bg-blue-500 text-white rounded-full flex items-center justify-center text-sm font-medium">2</div>
                    <span className="text-gray-700 text-base">자동 요약 및 문제 생성</span>
                  </div>
                  <div className="flex items-center gap-4">
                    <div className="w-10 h-10 bg-blue-500 text-white rounded-full flex items-center justify-center text-sm font-medium">3</div>
                    <span className="text-gray-700 text-base">문제 풀이 및 학습</span>
                  </div>
                </div>
              </div>
              <div className="bg-green-50 rounded-xl p-5">
                <p className="text-green-800 text-base leading-relaxed">
                  <span className="font-medium">100% 무료</span> • 자동 처리 • 즉시 결과
                </p>
              </div>
            </div>

            {/* 오른쪽: 업로드 */}
            <div className="bg-gray-50 rounded-xl p-6">
              <div className="text-center">
                <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
                  <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-800 mb-2">음성 파일 업로드</h3>
                <p className="text-gray-600 text-sm mb-4">MP3, M4A, WAV 파일을 지원합니다</p>

                <label className="block w-full bg-blue-500 hover:bg-blue-600 text-white font-medium py-3 px-6 rounded-lg cursor-pointer transition-colors">
                  파일 선택
                  <input
                    type="file"
                    accept=".m4a,.mp3,.wav"
                    onChange={handleFileUpload}
                    className="hidden"
                  />
                </label>
              </div>

              {selectedFile && (
                <div className="mt-4 space-y-3">
                  <div className="bg-white rounded-lg p-3 border">
                    <p className="text-sm font-medium text-gray-800">{selectedFile.name}</p>
                    <p className="text-xs text-gray-500">{(selectedFile.size / 1024 / 1024).toFixed(2)} MB</p>
                  </div>
                  <div className="flex gap-2">
                    <button className="flex-1 bg-green-500 hover:bg-green-600 text-white font-medium py-2 px-4 rounded-lg transition-colors text-sm">
                      요약 다운로드
                    </button>
                    <button
                      onClick={handleStartQuiz}
                      className="flex-1 bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-lg transition-colors text-sm"
                    >
                      문제 풀기
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (currentStep === 'quiz') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
        <div className="max-w-4xl mx-auto">
          {/* 네비게이션 */}
          <div className="flex justify-between items-center mb-6">
            <button
              onClick={goHome}
              className="bg-white hover:bg-gray-50 text-gray-700 font-medium py-2 px-4 rounded-lg transition-colors"
            >
              메인으로
            </button>
            <div className="text-center">
              <h1 className="text-2xl font-medium text-gray-800">문제 풀이</h1>
              <p className="text-sm text-gray-600">
                {currentQuestionIndex + 1} / {filteredQuestions.length}
              </p>
            </div>
            <button
              onClick={goBack}
              disabled={currentQuestionIndex === 0}
              className="bg-white hover:bg-gray-50 text-gray-700 font-medium py-2 px-4 rounded-lg transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              뒤로 가기
            </button>
          </div>

          {/* 필터 */}
          <div className="bg-white rounded-lg p-4 mb-6">
            <div className="flex flex-wrap gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">문제 유형</label>
                <select
                  value={filterType}
                  onChange={(e) => setFilterType(e.target.value as any)}
                  className="border border-gray-300 rounded-lg px-3 py-2 text-sm"
                >
                  <option value="all">전체</option>
                  <option value="multiple">객관식</option>
                  <option value="subjective">주관식</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">난이도</label>
                <select
                  value={filterDifficulty}
                  onChange={(e) => setFilterDifficulty(e.target.value as any)}
                  className="border border-gray-300 rounded-lg px-3 py-2 text-sm"
                >
                  <option value="all">전체</option>
                  <option value="easy">쉬움</option>
                  <option value="medium">보통</option>
                  <option value="hard">어려움</option>
                </select>
              </div>
              <div className="flex items-end">
                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    checked={showIncorrectOnly}
                    onChange={(e) => setShowIncorrectOnly(e.target.checked)}
                    className="rounded"
                  />
                  <span className="text-sm text-gray-700">틀린 문제만 보기</span>
                </label>
              </div>
            </div>
          </div>

          {/* 문제 */}
          <div className="bg-white rounded-lg p-6 shadow-lg">
            <div className="mb-4">
              <span className="inline-block bg-blue-100 text-blue-800 text-xs font-medium px-2 py-1 rounded-full mb-2">
                {currentQuestion.type === 'multiple' ? '객관식' : '주관식'} • {currentQuestion.difficulty}
              </span>
            </div>

            <h2 className="text-xl font-medium text-gray-800 mb-6 leading-relaxed">
              {currentQuestion.question}
            </h2>

            {currentQuestion.type === 'multiple' ? (
              <div className="space-y-3 mb-6">
                {currentQuestion.options?.map((option, index) => (
                  <label
                    key={index}
                    className={`block p-4 border-2 rounded-lg cursor-pointer transition-colors ${
                      selectedAnswer === option
                        ? 'border-blue-500 bg-blue-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <input
                      type="radio"
                      name="answer"
                      value={option}
                      checked={selectedAnswer === option}
                      onChange={(e) => handleAnswerSelect(e.target.value)}
                      className="sr-only"
                    />
                    <span className="text-gray-800 text-base">{option}</span>
                  </label>
                ))}
              </div>
            ) : (
              <div className="mb-6">
                <textarea
                  value={selectedAnswer}
                  onChange={(e) => handleAnswerSelect(e.target.value)}
                  placeholder="답을 입력해주세요..."
                  className="w-full p-4 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-base"
                  rows={4}
                />
              </div>
            )}

            <div className="flex justify-end">
              <button
                onClick={handleNextQuestion}
                disabled={!selectedAnswer}
                className="bg-blue-500 hover:bg-blue-600 disabled:bg-gray-300 text-white font-medium py-3 px-6 rounded-lg transition-colors disabled:cursor-not-allowed"
              >
                {currentQuestionIndex === filteredQuestions.length - 1 ? '결과 보기' : '다음'}
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
