import React, { useState } from 'react';
import './App.css';

// 샘플 데이터 타입 정의
interface Question {
  id: number;
  type: 'multiple' | 'subjective';
  difficulty: 'easy' | 'medium' | 'hard';
  question: string;
  options?: string[];
  correctAnswer: string;
  userAnswer?: string;
  isCorrect?: boolean;
}

// 샘플 문제 데이터
const sampleQuestions: Question[] = [
  {
    id: 1,
    type: 'multiple',
    difficulty: 'easy',
    question: 'FineTeaching의 주요 기능은 무엇인가요?',
    options: ['음성 요약', '문제 생성', '학습 보조', '모든 것'],
    correctAnswer: '모든 것'
  },
  {
    id: 2,
    type: 'subjective',
    difficulty: 'medium',
    question: 'FineTeaching에서 지원하는 음성 파일 형식을 두 가지 이상 작성하세요.',
    correctAnswer: 'm4a, mp3'
  },
  {
    id: 3,
    type: 'multiple',
    difficulty: 'hard',
    question: '문제풀이 인터페이스에서 제공하는 필터 기능이 아닌 것은?',
    options: ['객관식만 보기', '주관식만 보기', '난이도 필터', '시간 제한 설정'],
    correctAnswer: '시간 제한 설정'
  },
  {
    id: 4,
    type: 'multiple',
    difficulty: 'medium',
    question: 'FineTeaching의 디자인 프레임워크는?',
    options: ['Bootstrap', 'Tailwind CSS', 'Material UI', 'Ant Design'],
    correctAnswer: 'Tailwind CSS'
  },
  {
    id: 5,
    type: 'subjective',
    difficulty: 'easy',
    question: 'FineTeaching의 서비스 설명을 한 문장으로 써보세요.',
    correctAnswer: '강의 음성을 자동으로 요약하고 문제를 생성해주는 학습 보조 웹앱입니다.'
  }
];

function App() {
  const [currentStep, setCurrentStep] = useState<'intro' | 'upload' | 'quiz'>('intro');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [questions, setQuestions] = useState<Question[]>(sampleQuestions);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState<{[key: number]: string}>({});
  const [filter, setFilter] = useState<{
    type: 'all' | 'multiple' | 'subjective';
    difficulty: 'all' | 'easy' | 'medium' | 'hard';
    showWrongOnly: boolean;
  }>({
    type: 'all',
    difficulty: 'all',
    showWrongOnly: false
  });

  // 네비게이션: 홈으로 이동
  const goHome = () => {
    setCurrentStep('intro');
    setSelectedFile(null);
    setCurrentQuestionIndex(0);
    setUserAnswers({});
    setFilter({ type: 'all', difficulty: 'all', showWrongOnly: false });
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      setCurrentStep('upload');
    }
  };

  const handleStartQuiz = () => {
    setCurrentStep('quiz');
    setCurrentQuestionIndex(0);
    setUserAnswers({});
  };

  const handleAnswerSubmit = (answer: string) => {
    const currentQuestion = filteredQuestions[currentQuestionIndex];
    const isCorrect = answer === currentQuestion.correctAnswer;
    setUserAnswers(prev => ({ ...prev, [currentQuestion.id]: answer }));
    setQuestions(prev => prev.map(q => q.id === currentQuestion.id ? { ...q, userAnswer: answer, isCorrect } : q));
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < filteredQuestions.length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
    }
  };
  const handlePrevQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(prev => prev - 1);
    }
  };

  const filteredQuestions = questions.filter(q => {
    if (filter.type !== 'all' && q.type !== filter.type) return false;
    if (filter.difficulty !== 'all' && q.difficulty !== filter.difficulty) return false;
    if (filter.showWrongOnly && q.isCorrect !== false) return false;
    return true;
  });
  const currentQuestion = filteredQuestions[currentQuestionIndex] || questions[0];

  // 인트로+업로드 통합 화면
  const [dragActive, setDragActive] = useState(false);
  const handleDrag = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') setDragActive(true);
    if (e.type === 'dragleave') setDragActive(false);
  };
  const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setSelectedFile(e.dataTransfer.files[0]);
    }
  };

  return (
    <div className="min-h-screen bg-white font-sans">
      {/* 상단 네비게이션 */}
      <nav className="w-full flex items-center justify-between px-8 py-4 border-b bg-white/90 sticky top-0 z-10">
        <div className="flex items-center gap-2">
          <svg className="w-8 h-8 text-indigo-500" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" stroke="#6366f1" strokeWidth="2"/><path d="M8 12h8M12 8v8" stroke="#6366f1" strokeWidth="2" strokeLinecap="round"/></svg>
          <span className="text-2xl font-extrabold text-indigo-700 tracking-tight">FineTeaching</span>
        </div>
        <div className="hidden md:flex gap-8 text-gray-600 font-medium text-base">
          <button className="hover:text-indigo-500 transition">서비스 소개</button>
          <button className="hover:text-indigo-500 transition">비즈니스</button>
          <button className="hover:text-indigo-500 transition">이용요금</button>
          <button className="hover:text-indigo-500 transition">앱 다운로드</button>
        </div>
        <div className="flex gap-3">
          <button className="bg-black text-white rounded-full px-5 py-2 font-semibold text-sm shadow hover:bg-gray-800 transition">개인용 시작하기</button>
          <button className="border border-black rounded-full px-5 py-2 font-semibold text-sm hover:bg-gray-100 transition">로그인</button>
        </div>
      </nav>
      {/* 메인 콘텐츠 영역 */}
      <div className="flex flex-col lg:flex-row items-center justify-between max-w-7xl mx-auto py-20 px-6 gap-16">
        {/* 좌측: 서비스 소개 */}
        <div className="flex-1 max-w-2xl">
          <div className="relative">
                         {/* 일러스트/이미지 영역 */}
             <div className="mb-8 relative">
               <div className="w-48 h-48 md:w-64 md:h-64 bg-gradient-to-br from-indigo-100 to-pink-100 rounded-full flex items-center justify-center relative">
                 <svg className="w-16 h-16 md:w-20 md:h-20 text-indigo-400" fill="none" stroke="currentColor" strokeWidth="1.5" viewBox="0 0 24 24">
                   <path strokeLinecap="round" strokeLinejoin="round" d="M12 19v2m0 0h-3m3 0h3m-3-2a7 7 0 01-7-7m14 0a7 7 0 01-7 7m0 0V5a3 3 0 016 0v7a3 3 0 01-6 0z" />
                 </svg>
                 <div className="absolute -top-2 -right-2 md:-top-3 md:-right-3 w-12 h-12 md:w-16 md:h-16 bg-yellow-200 rounded-full flex items-center justify-center">
                   <svg className="w-6 h-6 md:w-8 md:h-8 text-yellow-600" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
                     <path strokeLinecap="round" strokeLinejoin="round" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                   </svg>
                 </div>
               </div>
             </div>
            {/* 텍스트 영역 */}
            <h1 className="text-5xl font-extrabold text-gray-900 leading-tight mb-6">
              음성에서<br/>
              <span className="text-indigo-500">학습 문제를</span><br/>
              만들어보세요
            </h1>
            <p className="text-xl text-gray-600 leading-relaxed mb-6">
              강의 음성 파일을 업로드하면 자동으로 요약하고,<br/>
              학습 내용을 바탕으로 객관식과 주관식 문제를 생성해드립니다.
            </p>
            <div className="flex items-center gap-2 text-lg">
              <span className="text-gray-600">100% 자동 및</span>
              <span className="bg-yellow-200 text-yellow-800 px-3 py-1 rounded-full font-semibold">무료</span>
            </div>
          </div>
        </div>
        {/* 우측: 음성 파일 업로드 */}
        <div className="flex-1 max-w-md">
          <div
            className={`bg-white rounded-2xl shadow-2xl p-8 border-2 ${dragActive ? 'border-indigo-400 bg-indigo-50' : 'border-gray-200'} transition-all duration-200 relative`}
            onDragEnter={handleDrag}
            onDragOver={handleDrag}
            onDragLeave={handleDrag}
            onDrop={handleDrop}
            onClick={() => document.getElementById('file-upload')?.click()}
          >
                         {/* 배경 장식 요소 */}
             <div className="absolute -top-2 -right-2 w-8 h-8 md:w-12 md:h-12 bg-yellow-200 rounded-full opacity-50"></div>
             <div className="absolute -bottom-2 -left-2 w-6 h-6 md:w-8 md:h-8 bg-indigo-200 rounded-full opacity-50"></div>

            <div className="relative z-10">
              <button className="w-full bg-indigo-500 hover:bg-indigo-600 text-white font-bold py-4 px-6 rounded-lg text-lg transition-colors mb-4">
                음성 파일 업로드
              </button>
              <p className="text-gray-500 text-sm text-center">
                또는 파일을 드래그하여 놓기, 음성 붙여넣기
              </p>
              <input
                type="file"
                accept=".m4a,.mp3,.wav"
                onChange={handleFileUpload}
                className="hidden"
                id="file-upload"
              />
              {selectedFile && (
                <div className="mt-6 p-4 bg-green-50 border border-green-200 rounded-lg text-green-800 text-center">
                  <span className="font-medium">{selectedFile.name}</span>
                  <span className="text-green-600 text-xs ml-2">({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)</span>
                </div>
              )}
              {selectedFile && (
                <div className="flex gap-3 mt-4">
                  <button className="flex-1 bg-green-500 hover:bg-green-600 text-white font-semibold py-3 px-4 rounded-lg transition-colors text-sm">
                    요약 다운로드
                  </button>
                  <button
                    onClick={handleStartQuiz}
                    className="flex-1 bg-indigo-500 hover:bg-indigo-600 text-white font-semibold py-3 px-4 rounded-lg transition-colors text-sm"
                  >
                    문제 풀기
                  </button>
                </div>
              )}
            </div>
          </div>
          {/* 하단 예시 썸네일 */}
          <div className="mt-6 text-center">
            <p className="text-gray-500 text-sm">음성 파일이 없으신가요?</p>
          </div>
        </div>
      </div>
      {/* 이하 기존 quiz 영역 등 유지 */}
      {currentStep === 'quiz' && (
        <section className="max-w-2xl mx-auto mt-16 px-4">
          <div className="bg-white/90 rounded-3xl shadow-2xl p-10 border border-indigo-100 animate-fadein">
              {/* 필터 섹션 */}
              <div className="mb-6 p-4 bg-indigo-50/60 rounded-xl flex flex-wrap gap-4 items-center justify-between">
                <div className="flex gap-2 items-center">
                  <select
                    value={filter.type}
                    onChange={(e) => setFilter(prev => ({ ...prev, type: e.target.value as any }))}
                    className="px-3 py-2 border border-indigo-200 rounded-md focus:ring-2 focus:ring-indigo-300"
                  >
                    <option value="all">모든 문제</option>
                    <option value="multiple">객관식만</option>
                    <option value="subjective">주관식만</option>
                  </select>
                  <select
                    value={filter.difficulty}
                    onChange={(e) => setFilter(prev => ({ ...prev, difficulty: e.target.value as any }))}
                    className="px-3 py-2 border border-indigo-200 rounded-md focus:ring-2 focus:ring-indigo-300"
                  >
                    <option value="all">모든 난이도</option>
                    <option value="easy">쉬움</option>
                    <option value="medium">보통</option>
                    <option value="hard">어려움</option>
                  </select>
                  <label className="flex items-center text-sm text-indigo-600">
                    <input
                      type="checkbox"
                      checked={filter.showWrongOnly}
                      onChange={(e) => setFilter(prev => ({ ...prev, showWrongOnly: e.target.checked }))}
                      className="mr-2 accent-indigo-500"
                    />
                    틀린 문제만 보기
                  </label>
                </div>
                <span className="text-xs text-indigo-400 font-mono">{filteredQuestions.length}문제</span>
              </div>
              {/* 문제 표시 */}
              <div className="mb-6">
                <div className="flex justify-between items-center mb-4">
                  <span className="text-sm text-indigo-400">
                    문제 {currentQuestionIndex + 1} / {filteredQuestions.length}
                  </span>
                  <span className={`px-2 py-1 rounded text-xs font-medium ${
                    currentQuestion.difficulty === 'easy' ? 'bg-green-100 text-green-800' :
                    currentQuestion.difficulty === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-red-100 text-red-800'
                  }`}>
                    {currentQuestion.difficulty === 'easy' ? '쉬움' :
                     currentQuestion.difficulty === 'medium' ? '보통' : '어려움'}
                  </span>
                </div>
                <h3 className="text-2xl font-bold text-indigo-700 mb-4">
                  {currentQuestion.question}
                </h3>
                {currentQuestion.type === 'multiple' && currentQuestion.options && (
                  <div className="space-y-3">
                    {currentQuestion.options.map((option, index) => (
                      <button
                        key={index}
                        onClick={() => handleAnswerSubmit(option)}
                        className={`w-full text-left p-3 rounded-xl border-2 transition-all duration-150 text-lg font-medium shadow-sm hover:scale-[1.02] ${
                          userAnswers[currentQuestion.id] === option
                            ? 'bg-indigo-100 border-indigo-400 text-indigo-700'
                            : 'bg-white border-indigo-100 hover:bg-indigo-50'
                        }`}
                      >
                        {option}
                      </button>
                    ))}
                  </div>
                )}
                {currentQuestion.type === 'subjective' && (
                  <div className="space-y-3">
                    <textarea
                      placeholder="답을 입력하세요..."
                      className="w-full p-3 border-2 border-indigo-100 rounded-xl resize-none text-lg focus:ring-2 focus:ring-indigo-300"
                      rows={3}
                      value={userAnswers[currentQuestion.id] || ''}
                      onChange={(e) => handleAnswerSubmit(e.target.value)}
                    />
                  </div>
                )}
                {userAnswers[currentQuestion.id] && (
                  <div className="mt-4 p-3 rounded-lg bg-indigo-50/60">
                    <p className="text-sm text-indigo-600">제출한 답: {userAnswers[currentQuestion.id]}</p>
                    {currentQuestion.isCorrect !== undefined && (
                      <p className={`text-sm font-medium ${
                        currentQuestion.isCorrect ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {currentQuestion.isCorrect ? '정답입니다!' : '틀렸습니다.'}
                      </p>
                    )}
                  </div>
                )}
              </div>
              {/* 네비게이션 버튼 */}
              <div className="flex justify-between gap-2">
                <button
                  onClick={goHome}
                  className="bg-gray-200 hover:bg-gray-300 text-indigo-700 font-semibold py-2 px-4 rounded-lg transition-colors"
                >
                  메인으로
                </button>
                <button
                  onClick={handlePrevQuestion}
                  disabled={currentQuestionIndex === 0}
                  className={`py-2 px-4 rounded-lg font-semibold transition-colors ${currentQuestionIndex === 0 ? 'bg-gray-100 text-gray-400 cursor-not-allowed' : 'bg-indigo-200 hover:bg-indigo-300 text-indigo-700'}`}
                >
                  뒤로 가기
                </button>
                {currentQuestionIndex < filteredQuestions.length - 1 && (
                  <button
                    onClick={handleNextQuestion}
                    className="bg-gradient-to-r from-indigo-500 to-pink-400 hover:from-indigo-600 hover:to-pink-500 text-white font-semibold py-2 px-4 rounded-lg transition-colors"
                  >
                    다음 문제
                  </button>
                )}
              </div>
            </div>
        </section>
      )}
    </div>
  );
}

export default App;
